$(document).ready(function(){$("a#comment-toggle").click(function(){var n=$(this).attr("comment-id");$("div#comment-controls-"+n).show();$("div.comments-body-"+n).text("");$.ajax({url:"/api/post/comments?postId="+n,success:function(t){$.each(t,function(t,i){$("div.comments-body-"+n).append($("<div/>").addClass("row p-2 my-2 border").append($("<div/>").addClass("my-2").append($("<p/>").addClass("m-0").text(i.creatorUserName)).append($("<sub />").text(i.createdOn))).append($("<p/>").addClass("px-4").text(i.content)))})},error:function(n){console.log(n)},beforeSend:function(){$("div.loader").show()},complete:function(){$("div.loader").hide();$("div.comments-body-"+n).show()},type:"GET",dataType:"json"})})});